sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	var uB = 'u_boolean';
	if (isLoading || newValue == oldValue || newValue == '' || g_form.getBooleanValue(uB)) {
		return;
	}

	//Type appropriate comment here, and begin script below
	// Field名のセット
	var setField0 = 'u_applicant_2_type';
	var setField1 = 'u_applicant_2_initial';
	var setField2 = 'u_applicant_2_key';
	// Scriptの多重起動防止
	g_form.setValue(uB, true);
	// 変数の用意
	var getType = g_form.getValue(setField0);
	var getKey = g_form.getValue(setField2);
	var getVal = g_form.getValue(setField1).trim();
	var valKey = [], valMsg = '';
	var ii,ret = 0;
	// タイプは、キーワード選択か？
	if(getType == 2 && getKey == '') {
		// キーワード選択なのに、選択肢が未記入
		getVal = '';
		valMsg = '選択肢を設定してから記入してください';
	} else if(getType == 2) {
		// キーワード選択である
		valKey = getKey.split(';');
		for(ii = 0; ii < valKey.length; ii++) {
			if(valKey[ii] == getVal) {
				ret = 1;
			}
		}
		if(ret == 0) {
			getVal = '';
			valMsg = '選択肢から選択してください';
		}
	}
	// テキストのときは、何もしない

	// 再代入
	g_form.setValue(setField1, getVal);
	// エラーメッセージがあるときは表示
	if(valMsg != '') {
		g_form.showErrorBox(setField1, valMsg);
	}
	// Scriptの多重起動防止解除
	g_form.setValue(uB, false);
}
