sys_ui_policy.script_true:
function onCondition() {
	var getVal, getSts, getRsv, ii;
	// フィールドのラベルを登録
	var setVal ='u_applicant_7';
	// ステータス状態のゲット
	getSts = g_form.getValue('u_state');
	// フィールドの名称を変更
	g_form.setLabelOf(setVal + '_input_value', g_form.getValue(setVal + '_rsv_name'));
	// 既存の値を保存
	getRsv = g_form.getValue(setVal + '_input_value');
	// フィールドの必須変更
	g_form.setMandatory(setVal + '_input_value', g_form.getBooleanValue(setVal + '_rsv_condition'));
	// 既存データはあったか？
	if(getRsv != ''){
		g_form.setValue(setVal + '_input_value', getRsv);
	} else {
		// 初期値があるか？
		getVal = g_form.getValue(setVal + '_rsv_initial');
		if(!(getVal == '' || getVal == 'null' || getVal == 'NULL')){
			g_form.setValue(setVal + '_input_value', getVal);
		}
	}
	// ステータスは、作成中以外か？
	if(getSts != 1){
		g_form.setReadOnly(setVal + '_input_value', true);
	}
}
